package test03_1;

public class Point {
	private int x;
	private int y;
	
	public Point() {
		// TODO Auto-generated constructor stub
		x = 10;
		y = 10;
	}
	
	public Point(int x, int y){
		this.x = x;
		this.y = y;
	}
	public int getX() {
		return x;
	}
	public void setX(int x) {
		this.x = x;
	}
	public int getY() {
		return y;
	}
	public void setY(int y) {
		this.y = y;
	}
	
	
}
